// Validate and sanitize input
const item = $input.item.json;

// Required fields validation
const required = ['topic', 'contentType', 'platform', 'brandGuidelines'];
for (const field of required) {
  if (!item[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Content type validation
const allowedTypes = ['post', 'caption', 'thread', 'article', 'story'];
if (!allowedTypes.includes(item.contentType)) {
  throw new Error(`Invalid content type: ${item.contentType}`);
}

// Platform validation
const allowedPlatforms = ['twitter', 'linkedin', 'instagram', 'tiktok', 'youtube'];
if (!allowedPlatforms.includes(item.platform)) {
  throw new Error(`Invalid platform: ${item.platform}`);
}

// PII Check - ensure no personal data in topic
const piiPatterns = [
  /\b[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}\b/i, // Email
  /\b\d{3}[-.]?\d{3}[-.]?\d{4}\b/, // Phone
  /\b\d{3}-\d{2}-\d{4}\b/, // SSN
  /\b(?:\d{4}[-\s]?){3}\d{4}\b/ // Credit card
];

for (const pattern of piiPatterns) {
  if (pattern.test(item.topic)) {
    throw new Error('PII detected in topic. Please remove personal information.');
  }
}

// Extract brand colors from guidelines
const brandColors = item.brandGuidelines.colors || {
  primary: process.env.BRAND_PRIMARY_COLOR || '#6200EA',
  secondary: process.env.BRAND_SECONDARY_COLOR || '#03DAC6'
};

// Character limits by platform
const charLimits = {
  twitter: 280,
  linkedin: 3000,
  instagram: 2200,
  tiktok: 150,
  youtube: 5000
};

return {
  topic: item.topic,
  contentType: item.contentType,
  platform: item.platform,
  tone: item.tone || 'professional',
  keywords: item.keywords || [],
  brandColors: brandColors,
  brandVoice: item.brandGuidelines.voice || 'informative and friendly',
  charLimit: charLimits[item.platform],
  requestId: `copy-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  timestamp: new Date().toISOString()
};

