const response = $input.item.json;
const input = $node["Validate & Sanitize Input"].json;

// Extract generated content
const content = response.choices[0].message.content;

// Platform-specific formatting
let formattedContent = content;

// Add platform-specific elements
switch(input.platform) {
  case 'twitter':
    // Ensure thread formatting if needed
    if (input.contentType === 'thread') {
      const tweets = content.split('\n\n').filter(t => t.trim());
      formattedContent = tweets.map((tweet, i) => `${i + 1}/${tweets.length} ${tweet}`).join('\n\n');
    }
    break;

  case 'instagram':
    // Add spacing for readability
    formattedContent = content.replace(/\n/g, '\n\n');
    break;

  case 'linkedin':
    // Professional formatting
    if (input.contentType === 'article') {
      formattedContent = `ðŸ“ ${content}`;
    }
    break;
}

// Brand compliance check
const brandCompliant = {
  hasKeywords: input.keywords.every(kw => 
    content.toLowerCase().includes(kw.toLowerCase())
  ),
  withinLimit: formattedContent.length <= input.charLimit,
  appropriate: !content.match(/\b(competitor1|competitor2|banned-word)\b/gi)
};

// Calculate engagement score (mock - would use historical data)
const engagementScore = Math.random() * 100;

return {
  requestId: input.requestId,
  platform: input.platform,
  contentType: input.contentType,
  content: formattedContent,
  charCount: formattedContent.length,
  charLimit: input.charLimit,
  brandCompliant: brandCompliant,
  engagementScore: engagementScore.toFixed(2),
  hashtags: (formattedContent.match(/#\w+/g) || []).join(' '),
  generatedAt: new Date().toISOString(),
  status: brandCompliant.withinLimit && brandCompliant.appropriate ? 'approved' : 'needs_review'
};

// Error handling and metrics
const error = $input.item.json;
const originalInput = $node["Validate & Sanitize Input"].json;

// Log error for monitoring
console.error('Content generation failed:', {
  requestId: originalInput.requestId,
  error: error.message || 'Unknown error',
  platform: originalInput.platform,
  timestamp: new Date().toISOString()
});

// Return error response
return {
  requestId: originalInput.requestId,
  status: 'failed',
  error: error.message || 'Content generation failed',
  timestamp: new Date().toISOString(),
  retryable: !error.message?.includes('Invalid') && !error.message?.includes('PII')
};
